import "./styles.css";

export function CheckoutProduct({ id, title, image, price, rating }) {
  return (
    <div className="checkoutProduct">
      <div className="product__info">
        <p>{title}</p>
        <p className="product__price">
          <small>R$ </small>
          <small>{price}</small>
        </p>
        <div className="product__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>⭐</p>
            ))}
        </div>
      </div>
      <img
        src={image}
        alt={title}
      />
      <button>Add to Basket</button>
    </div>
  );
}
